#!/bin/bash
source initialize.sh
curl -s --insecure --cert $SSLPATH/private_full_node.crt --key $SSLPATH/private_full_node.key -d '{}' -H "Content-Type: application/json" -X POST https://$TARGETIP:8555/get_blockchain_state | jq -r '.blockchain_state | "chia_rpc_blockchain_state_difficulty{coin=\"chia\",service_type=\"full_node\"} \(.difficulty)"'
curl -s --insecure --cert $SSLPATH/private_full_node.crt --key $SSLPATH/private_full_node.key -d '{}' -H "Content-Type: application/json" -X POST https://$TARGETIP:8555/get_blockchain_state | jq -r '.blockchain_state | "chia_rpc_blockchain_state_fee_amount{coin=\"chia\",service_type=\"full_node\"} \(.peak.fees)"'
curl -s --insecure --cert $SSLPATH/private_full_node.crt --key $SSLPATH/private_full_node.key -d '{}' -H "Content-Type: application/json" -X POST https://$TARGETIP:8555/get_blockchain_state | jq -r '.blockchain_state | "chia_rpc_blockchain_state_height{coin=\"chia\",service_type=\"full_node\"} \(.peak.height)"'
curl -s --insecure --cert $SSLPATH/private_full_node.crt --key $SSLPATH/private_full_node.key -d '{}' -H "Content-Type: application/json" -X POST https://$TARGETIP:8555/get_blockchain_state | jq -r '.blockchain_state | "chia_rpc_blockchain_state_netspace{coin=\"chia\",service_type=\"full_node\"} \(.space)"'
curl -s --insecure --cert $SSLPATH/private_full_node.crt --key $SSLPATH/private_full_node.key -d '{}' -H "Content-Type: application/json" -X POST https://$TARGETIP:8555/get_blockchain_state | jq -r '.blockchain_state | "chia_rpc_blockchain_state_synced{coin=\"chia\",service_type=\"full_node\"} \(.sync.synced)"'